package Basics;

public class PrintingStringAndIntegers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		int y = 10;
		int z = 15;
		System.out.println("String"+x+y+z);
		System.out.println(x+"String"+y+z);
		System.out.println(x+y+"String"+z);

	}

}
