package operators;

public class ArithmaticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		int y = 10;
		System.out.println(x+y);
		System.out.println(x-y);
		System.out.println(x*y);
		System.out.println(x/y);
		System.out.println(x%y);

	}

}
