package operators;

public class BitwiseOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		int y = 2;
		System.out.println(x&y);
		System.out.println(x|y);
		System.out.println(~x);
		System.out.println(x^y);
		System.out.println(x<<1);
		System.out.println(x>>1);

	}

}
