package operators;

public class UnaryOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		System.out.println(x++);
		System.out.println(x);
		
		System.out.println(++x);
		System.out.println(x);
		
		x--;
		System.out.println(x);
		
		--x;
		System.out.println(x);

	}

}

