package practice;

public class CalculateSimpleInterest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double principle = 900;
		double rate = 3.4;
		double time = 2;
		
		double si = (principle*rate*time)/100;
		
		System.out.println("Simple Interest : "+si);

	}

}
